root {
  --glass-bg-primary: rgba(255, 255, 255, 0.1);
  --glass-bg-card: rgba(255, 255, 255, 0.08);
  --glass-border: rgba(255, 255, 255, 0.12);
  --glass-shadow: 0 8px 32px 0 rgba(0, 0, 0, 0.37);
  --glass-blur: blur(10px);
  --primary-color: #ff3a7c;
  --primary-light: #ff7eac;
  --primary-dark: #d62f68;
  --text-color: #f8f9fa;
  --text-muted: rgba(255, 255, 255, 0.6);
  --accent-purple: #6c5ce7;
  --accent-blue: #56b3ff;
  --gradient-primary: linear-gradient(135deg, #ff3a7c 0%, #6c5ce7 100%);
  --gradient-bg: linear-gradient(135deg, #30108f 0%, #8019b0 50%, #3a1288 100%);
}

body {
  font-family: 'Montserrat', sans-serif;
  background: var(--gradient-bg);
  color: var(--text-color);
  line-height: 1.5;
  min-height: 100vh;
  overflow-x: hidden;
  position: relative;
}

body::before {
  content: '';
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-image: 
    radial-gradient(circle at 20% 20%, rgba(255, 58, 124, 0.2) 0%, transparent 25%),
    radial-gradient(circle at 80% 80%, rgba(108, 92, 231, 0.2) 0%, transparent 25%),
    radial-gradient(circle at 50% 50%, rgba(86, 179, 255, 0.1) 0%, transparent 50%);
  z-index: -1;
}

/* Glassmorphism card styles */
.glass-card {
  background: var(--glass-bg-card);
  border-radius: 16px;
  box-shadow: var(--glass-shadow);
  backdrop-filter: var(--glass-blur);
  -webkit-backdrop-filter: var(--glass-blur);
  border: 1px solid var(--glass-border);
  padding: 20px;
  transition: all 0.3s ease;
}

.glass-card:hover {
  transform: translateY(-3px);
  box-shadow: 0 12px 40px 0 rgba(0, 0, 0, 0.45);
  border: 1px solid rgba(255, 255, 255, 0.18);
}

/* Control panel glassmorphism */
.control-panel {
  width: 360px;
  background: rgba(25, 20, 65, 0.7);
  box-shadow: var(--glass-shadow);
  backdrop-filter: var(--glass-blur);
  -webkit-backdrop-filter: var(--glass-blur);
  border-right: 1px solid var(--glass-border);
  padding: 1.5rem 1.5rem 80px;
  display: flex;
  flex-direction: column;
  overflow-y: auto;
  z-index: 10;
  position: relative;
}

.app-title {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  margin-bottom: 2rem;
  flex-wrap: wrap;
}

.app-title .icon {
  font-size: 2rem;
  background: var(--gradient-primary);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

.app-title h1 {
  font-size: 1.5rem;
  font-weight: 700;
  letter-spacing: -0.5px;
  color: var(--text-color);
  margin: 0;
}

/* Form elements */
.form-group {
  margin-bottom: 1.25rem;
}

label {
  display: block;
  font-size: 0.875rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
  color: var(--text-color);
}

.textarea {
  width: 100%;
  padding: 0.75rem;
  font-family: 'Open Sans', sans-serif;
  border: 1px solid var(--glass-border);
  border-radius: 12px;
  background: rgba(255, 255, 255, 0.05);
  color: var(--text-color);
  transition: all 0.2s;
  resize: vertical;
  min-height: 100px;
  backdrop-filter: var(--glass-blur);
  -webkit-backdrop-filter: var(--glass-blur);
}

.textarea:focus {
  outline: none;
  border-color: var(--primary-color);
  box-shadow: 0 0 0 3px rgba(255, 58, 124, 0.3);
}

/* Button styles */
.btn {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  padding: 0.75rem 1.25rem;
  font-weight: 600;
  border-radius: 12px;
  cursor: pointer;
  transition: all 0.3s;
  border: none;
  backdrop-filter: var(--glass-blur);
  -webkit-backdrop-filter: var(--glass-blur);
  text-decoration: none;
}

.btn-primary {
  background: var(--gradient-primary);
  color: white;
  box-shadow: 0 4px 15px rgba(255, 58, 124, 0.4);
  width: 100%;
}

.btn-primary:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(255, 58, 124, 0.5);
}

.btn-primary:active {
  transform: translateY(0);
}

.btn-outline {
  background: rgba(255, 255, 255, 0.1);
  color: var(--text-color);
  border: 1px solid var(--glass-border);
}

.btn-outline:hover {
  background: rgba(255, 255, 255, 0.15);
  border-color: rgba(255, 255, 255, 0.25);
}

.btn i {
  font-size: 1.25rem;
}

/* Size, quality, and style buttons */
.size-btn, .quality-btn, .style-type-btn, .style-preset-btn, .count-btn {
  flex: 1;
  padding: 10px 12px;
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid var(--glass-border);
  border-radius: 12px;
  font-size: 0.875rem;
  font-weight: 600;
  color: var(--text-muted);
  cursor: pointer;
  transition: all 0.2s;
  text-align: center;
  backdrop-filter: var(--glass-blur);
  -webkit-backdrop-filter: var(--glass-blur);
}

.size-btn:hover, .quality-btn:hover, .style-type-btn:hover, .style-preset-btn:hover, .count-btn:hover {
  background: rgba(255, 255, 255, 0.1);
}

.size-btn.active, .quality-btn.active, .style-type-btn.active, .style-preset-btn.active, .count-btn.active {
  background: linear-gradient(135deg, rgba(255, 58, 124, 0.2) 0%, rgba(108, 92, 231, 0.2) 100%);
