.single-image-layout .gallery-wrapper {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 90%;
}

.single-image-layout .image-wrapper {
  max-width: 90%;
  max-height: 90%;
  position: relative;
  cursor: pointer;
  border-radius: 16px;
  overflow: hidden;
  box-shadow: var(--glass-shadow);
  backdrop-filter: var(--glass-blur);
  -webkit-backdrop-filter: var(--glass-blur);
}

.two-image-layout .gallery-wrapper {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  gap: 20px;
  width: 100%;
  height: 90%;
  justify-content: center;
  align-items: center;
}

.two-image-layout .image-wrapper {
  flex: 0 0 calc(45% - 10px);
